package model; public class PedidoItem { private Alimento alimento; private int quantidade; private double precoUnitario; public PedidoItem(){} public PedidoItem(Alimento a,int q,double p){this.alimento=a;this.quantidade=q;this.precoUnitario=p;} public Alimento getAlimento(){return alimento;} public void setAlimento(Alimento a){this.alimento=a;} public int getQuantidade(){return quantidade;} public void setQuantidade(int q){this.quantidade=q;} public double getPrecoUnitario(){return precoUnitario;} public void setPrecoUnitario(double p){this.precoUnitario=p;} }
